.pio .controls {
  margin-top: 1em;
}
.pio .selected-placeholder {
  display: block;
  margin-left: 1em;
}
.pio .selected-boards {
  display: none;
}
.pio .unselect {
  margin-left: 1em;
}
.pio select.form-control,
.pio input.form-control {
  color: #747369;
  border-color: transparent;
  background-color: #1e1e1e;
}
.pio select.form-control:hover,
.pio input.form-control:hover,
.pio select.form-control:focus,
.pio input.form-control:focus {
  box-shadow: none;
  background-color: #1e1e1e;
}
.pio .command-status {
  display: none;
  text-align: center;
  color: #6699cc;
  font-size: 16.5px;
}
.pio .recent-projects-wrapper {
  display: flex;
  flex-direction: column;
}
.pio .recent-projects-wrapper .recent-project-item {
  border-radius: 2px;
  border: 1px solid rgba(0, 0, 0, 0.5);
  padding: 10px;
  margin-bottom: 10px;
}
.pio .recent-projects-wrapper .recent-project-item .recent-project-title {
  display: block;
}
.pio .recent-projects-wrapper .recent-project-item .recent-project-path {
  color: #747369;
  word-wrap: break-word;
  /* Using non-breaking space so that it won't break righ after brace
           when path is too long and it does not fit into single line. */
}
.pio .recent-projects-wrapper .recent-project-item .recent-project-path::before {
  content: '[\00A0';
}
.pio .recent-projects-wrapper .recent-project-item .recent-project-path::after {
  content: '\00A0]';
}
.pio .recent-projects-wrapper .recent-project-item:hover {
  background-color: rgba(255, 255, 255, 0.07);
  cursor: pointer;
}
.pio .version-wrapper .version {
  margin: auto 0.5em;
  white-space: nowrap;
}
.pio .version-wrapper .version .version-string {
  margin-left: 0.5em;
}
.pio .version-wrapper .version-copy:hover {
  color: #9a998f;
  cursor: pointer;
}
.pio .pio-versions .upgrade-wrapper {
  display: none;
}
.pio .pio-versions .upgrade-wrapper > span {
  padding: 10px;
}
.pio .pio-versions .upgrade-wrapper .do-upgrade:hover {
  cursor: pointer;
}
.install-progress {
  width: 100%;
  margin-bottom: 1em;
}
.pio-about {
  text-align: center;
}
.pio-about.about {
  padding-top: 2em ;
  padding-bottom: 2em;
}
.pio-about-item {
  margin-top: 1em;
  margin-bottom: 1em;
}
.with-love .icon-heart {
  color: #f2777a;
}
.directory-select-wrapper {
  margin-top: 1em;
}
.pio .boards-loading-placeholder {
  text-align: center;
}
.pio-donate .links .btn {
  font-size: 1.3em;
}
.pio-donate .lead {
  margin: 0.5em;
  line-height: 1.5em;
  font-size: 13.2px;
}
.pio-import-arduino-ide-project .pick-directory-wrapper {
  text-align: center;
  margin-top: 1em;
  margin-bottom: 2em;
}
.pio-import-arduino-ide-project .lib-manager-input-wrapper {
  display: none;
}
.pio-serial-monitor .advanced-settings {
  display: none;
}
.tree-view .pio-active-directory {
  /* Matches all selected items (those having lighen background). Note the space between "&" and "." */
  /* Matches the root directory only. Note that there are no space between "&" and "." */
}
.tree-view .pio-active-directory.project-root {
  border-left: 2px solid #6699cc;
}
.tree-view .pio-active-directory .selected::before {
  margin-left: 7px;
}
.tree-view .pio-active-directory.selected::before {
  border-right: 4px solid #6699cc;
  border-top-right-radius: 4px;
  border-bottom-right-radius: 4px;
}
.pio-home-screen-hr {
  border-top: 1px solid #1e1e1e;
  width: 100%;
}
.pio-project-examples-progress {
  display: none;
  width: 100%;
  margin-bottom: 1em;
  margin-top: 1em;
}
.pio-quick-access-wrapper {
  max-width: 300px;
  margin: 0 auto;
}
.pio-home-quick-access {
  display: flex;
  flex-direction: column;
}
.pio-home-quick-access > a {
  margin-bottom: 10px;
  padding: 5px 20px;
  text-align: left;
}
.pio-home-quick-access > a:before {
  margin-right: 10px;
  min-width: 14px;
}
.pio-web-links {
  margin: 20px 0px;
}
.pio-libraries {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  -webkit-user-select: none;
  cursor: default;
  overflow: auto;
  font-size: 12.1px;
  line-height: 1.4;
  padding: 20px;
  color: #747369;
  background-color: #262626;
}
